void teststrlarge()
{
	string net1 = "192.168.1.1/24";
	string net2 = "192.168.1.1";
	string mask = "255.255.255.0";

	int number = 3;
	int n1 = net1.size();
	int n2 = net2.size();
	int n3 = mask.size();
	int n  = n1 + n2 + n3;
	n += 3;

	char* p = new char[n * number + 1 + 4 + 4];

	char* pt = p;
	*((int*)pt) = 0x12345678;
	pt += 4;
	*((int*)pt) = 0x123456;
	pt += 4;
	int index = 0;
	for (int i = 0; i < number; ++i)
	{
		printf("%d\n", ++index);
		memcpy(pt, net1.c_str(), n1);
		pt += n1;
		*pt = ';';
		pt += 1;

		memcpy(pt, net2.c_str(), n2);
		pt += n2;
		*pt = ';';
		pt += 1;

		memcpy(pt, mask.c_str(), n3);
		pt += n3;
		*pt = ';';
		pt += 1;
	}

	string str(p + 4 + 4, n * number);

	list<string> lstSubnetRet;
	CStrWork::MakeListByTok(str, lstSubnetRet, ';');
	
	CDebugLog::WriteTimeFmtLog("12345678.log", CDebugLog::NORMALLOG, "[]%s", str.c_str()); ;
}

